import java.util.ArrayList;
import java.util.Arrays;

public class Show {
	private ArrayList<Slot> slots;
	public Show(){
		this.slots=new ArrayList<Slot>();
	}


	public ArrayList<Slot> getSlots() {
		return slots;
	}


	public int getNumberOfSlots() {
		// TODO Auto-generated method stub
		return slots.size();
	}


}
